int foo(){ return 0;}

int main()
{
    int (*p)() = foo;

    (*p)(); // call foo
    p();    // call foo too

    printf("%p\n, &p)   // address of pointer p

    printf("%p\n, *p)       // address of foo
    printf("%p\n, **p)      // address of foo
